`define NUM0 '
`define NUM1 '
`define NUM2 '
`define NUM3 '
`define NUM4 '
`define NUM5 '
`define NUM6 '
`define NUM7 '
`define NUM8 '
`define NUM9 '

module num_to_image(
    input  logic [3:0]        num,
    output logic [(784<<3)-1] image);

    always_comb begin
        case (num)
            4'd0: image = NUM0; 
            4'd1: image = NUM1;
            4'd2: image = NUM2;
            4'd3: image = NUM3;
            4'd4: image = NUM4;
            4'd5: image = NUM5;
            4'd6: image = NUM6;
            4'd7: image = NUM7;
            4'd8: image = NUM8;
            4'd9: image = NUM9;
            default: image = 'd0;
        endcase
    end

endmodule: num_to_image
